BEGIN TRANSACTION;
CREATE TABLE privacy (  id INTEGER PRIMARY KEY,  name TEXT);
CREATE TABLE icons (  id INTEGER PRIMARY KEY AUTOINCREMENT,  mime_type TEXT,  width INTEGER NOT NULL,  height INTEGER NOT NULL,  name TEXT,  data BLOB);
DELETE FROM sqlite_sequence;
CREATE TABLE users (  id INTEGER PRIMARY KEY AUTOINCREMENT,  username TEXT UNIQUE,  realname TEXT,  email TEXT,  website TEXT,  avatar INTEGER,  FOREIGN KEY( avatar ) REFERENCES icons(id));
CREATE TABLE collections (  id INTEGER PRIMARY KEY AUTOINCREMENT,  created DATETIME,   modified DATETIME,   title TEXT,  description TEXT,  owner INTEGER NOT NULL,  privacy INTEGER NOT NULL,  FOREIGN KEY( owner ) REFERENCES users(id),  FOREIGN KEY( privacy ) REFERENCES privacy(id));
CREATE TABLE achievements (  id INTEGER PRIMARY KEY AUTOINCREMENT,  title TEXT,  description TEXT,  need_icon INTEGER,  have_icon INTEGER,  collection INTEGER NOT NULL,  FOREIGN KEY( have_icon ) REFERENCES icons(id),  FOREIGN KEY( need_icon ) REFERENCES icons(id),  FOREIGN KEY( collection ) REFERENCES collections(id));
CREATE TABLE haves (  user INTEGER,  achievement INTEGER,   earned DATETIME,   FOREIGN KEY( user ) REFERENCES user(id),  FOREIGN KEY( achievement ) REFERENCES achievement(id));
CREATE TABLE user_follows (  follower INTEGER,   followee INTEGER,   FOREIGN KEY( follower ) REFERENCES user(id),  FOREIGN KEY( followee ) REFERENCES user(id) );
CREATE TABLE collection_follows (  user INTEGER,   collection INTEGER,   FOREIGN KEY( user ) REFERENCES users(id),  FOREIGN KEY( collection ) REFERENCES collections(id) );
COMMIT;
